import React, { useState, useContext } from 'react'
import { useNavigate } from 'react-router-dom'
import { AccountContext } from '../context/AccountContext'
import { ProfileContext } from '../context/ProfileContext'
import { GameContext } from '../context/GameContext'
import styled from 'styled-components'

import Form from '../components/Form'
import Input from '../components/Input'
import FileInput from '../components/FileInput'
import Checkbox from '../components/Checkbox'
import Submit from '../components/Submit'

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  max-width: 60vw;
  min-width: 240px;
  max-height: 80vh;
  min-height: 240px;
  border: 1px solid black;
  border-radius: 10px;
  padding: 20px;
  box-shadow: 5px 5px 18px 5px rgba(0,0,0,0.4);
  background-color: lightgrey;
  overflow: hidden auto;
`;

const JokeBox = styled.p`
  margin-top: 10px;
  display: flex;
  height: 30%;
`;

const Button = styled.button`
  margin-top: 10px;
  padding: 5px 12px;
  border: 1px solid black;
  height: 30px;
  width: fit-content;
  border-radius: 4px;
  cursor: pointer;
`;

const Section = styled.div`
  margin-top: 10px;
`;

const Photo = styled.img`
  border-radius: 50%;
  border: 1px solid black;
  width: 50px;
  height: 50px;
`;

const DashboardPage = () => {

  const {
    id,
    email,
    password,
    setId,
    setEmail,
    setPassword
  } = useContext(AccountContext);

  const {
    username,
    soundOn,
    darkModeOn,
    useSwipeOn,
    best,
    rank,
    fileName,
    photo,
    setUsername,
    setSoundOn,
    setDarkModeOn,
    setUseSwipeOn,
    setBest,
    setRank,
    setFileName,
    setPhoto } = useContext(ProfileContext);

  const {
    score,
    multiplier,
    tileIds,
    tiles,
    setScore,
    setMultiplier,
    setTileIds,
    setTiles
  } = useContext(GameContext);

  const [dadJoke, setDadJoke] = useState('- - - - - -');
  const navigate = useNavigate();

  const getDadJoke = async () => {

    const response = await fetch('https://icanhazdadjoke.com/', {
      method: 'GET',
      headers: { Accept: 'application/json' }
    });

    const jokeData = await response.json();

    setDadJoke(jokeData.joke);

  }

  return (
    <Form onSubmit={{}}>
      <h1>Dashboard</h1>
      <Section>
        <Photo />
      </Section>
      <Section></Section>
      <Input
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        type='email'
        placeholder='Email Address'
      />
      <Input
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        type='password'
        placeholder='Password'
      />
      <Input
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        type='text'
        placeholder='Username'
      />
      <FileInput
        onChange={(e) => setPhoto(e.target.files[0])}
        type='file'
        accept="image/jpg, image/jpeg, image/png, image/gif"
      />
      <Option>
        <label htmlFor='soundOn'>Sound On:</label>
        <Checkbox
          checked={soundOn}
          onChange={() => { setSoundOn(!soundOn) }}
          type='checkbox'
          name='oundOn'
        />
      </Option>
      <Option>
        <label htmlFor='darkModeOn'>Dark Mode On:</label>
        <Checkbox
          checked={darkModeOn}
          onChange={() => { setDarkModeOn(!darkModeOn) }}
          type='checkbox'
          name='darkModeOn'
        />
      </Option>
      <Option>
        <label htmlFor='useSwipe'>Use Swipe On:</label>
        <Checkbox
          checked={useSwipeOn}
          onChange={() => { setUseSwipeOn(!useSwipeOn) }}
          type='checkbox'
          name='useSwipe'
        />
      </Option>
      <Submit
        value='Register'
        type='submit'
      />
      <Button onClick={() => { navigate('/') }}>
        Logout
      </Button>
    </Form>
  )
}

export default DashboardPage
